}


class Contact extends  React.Component {

    state = {
        name: "Test",
        cellphone: "Test",
        homephone: "Test",
        workphone: "Test",
        email: "Test"
    }

    
    render() {    
        return (
            <>

                <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.2/semantic.min.css"/>
                <List>
                    <Icon  name='address book' /> <p>Name: {this.state.name} </p> 
                    <Icon  name='phone' /> <p>Cell Phone: {this.state.cellphone} </p> 
                    <Icon  name='phone' /> <p>Home Phone: {this.state.homephone} </p> 
                    <Icon  name='phone' /> <p>Work Phone: {this.state.workphone} </p> 
                    <Icon  name='envelope' /> <p>Email: {this.state.email} </p>
                </List>
                <Button floated="right" icon="pencil" onClick={handleEdit} /> <br /> <br />
                <Modal trigger={<Button floated="right" >Edit</Button>} closeIcon>
                    <Header icon='archive' content='Archive Old Messages' />
                    <Modal.Content>
                    <p>
                        Are you sure you want to delete this contact?
                    </p>
                    </Modal.Content>
                    <Modal.Actions>
                    <Button color='red'>
                        <Icon name='remove' /> No
                    </Button>
                    <Button color='green' onClick={handleDelete}>
                        <Icon name='checkmark' /> Yes
                    </Button>
                    </Modal.Actions>    
                </Modal>
                <br /><br />
                <Divider /> 
            </>

        )

    
    }
}
export default Contact
