}


function Contact() {

    var contact = this.props.contacts

        return (
            <>

                <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.2/semantic.min.css"/>
                <List>
                    <Icon  name='address book' /> <p>Name: {contact.name} </p> 
                    <Icon  name='phone' /> <p>Cell Phone: {contact.cellphone} </p> 
                    <Icon  name='phone' /> <p>Home Phone: {contact.homephone} </p> 
                    <Icon  name='phone' /> <p>Work Phone: {contact.workphone} </p> 
                    <Icon  name='envelope' /> <p>Email: {contact.email} </p>
                </List>
                <Button floated="right" icon="pencil" onClick={handleEdit} /> <br /> <br />
                <Modal trigger={<Button floated="right" >Edit</Button>} closeIcon>
                    <Header icon='archive' content='Archive Old Messages' />
                    <Modal.Content>
                    <p>
                        Are you sure you want to delete this contact?
                    </p>
                    </Modal.Content>
                    <Modal.Actions>
                    <Button color='red'>
                        <Icon name='remove' /> No
                    </Button>
                    <Button color='green' onClick={handleDelete}>
                        <Icon name='checkmark' /> Yes
                    </Button>
                    </Modal.Actions>    
                </Modal>
                <br /><br />
                <Divider /> 
            </>

        )
}
export default Contact
